%Para la escritura del vectorizeclcclear %limpiamos la ventana de comandos%Se lee el archivo de audio x, almacenado en la actual carpeta de trabajo%Establecemos en octavepkg load signal;pkg load symbolic;pkg load optim;[x, fs] = audioread('grabacion.wav'); lx = length(x);ganancia = 20*(log10(max(abs(x))));t = linspace(0, length(x)/fs, length(x));n=0:lx-1;figure(2);subplot(1,1,1);stem(n, x);title('X[n]');%subplot(2,1,2);%stem(n,y);%title('Y[n]');[x]=grabaraudio(t,fs) 